      // 초기값 설정
      act(() => storage.set({ theme: "light" }));
      expect(result.current).toEqual({ theme: "light" });

      act(() => {
        const newData = { theme: "dark" };
        localStorage.setItem("cross-tab-test", JSON.stringify(newData));

        window.dispatchEvent(
          new StorageEvent("storage", {
