├── useDiscount.ts              # 할인 정책 계산 훅
├── useLightningSaleTimer.ts    # 번개 세일 타이머 관리 훅
├── useOrderSummary.ts          # 주문 요약(합계 등) 계산 훅
├── usePoint.ts                 # 포인트 정책 계산 훅
├── useSuggestionSaleTimer.ts   # 제안 세일 타이머 관리 훅
